var currentLanguage=null;var languagePath='/lang/';function setLanguage(lang){currentLanguage=lang;var jsonFile=languagePath+currentLanguage+".json";$.getJSON(jsonFile,function(langData){$("[translate]").each(function(){var translationAttr=$(this).attr('translate');var keyPathTokens=translationAttr.split('.');var currObjectValue=langData;keyPathTokens.forEach(key=>{if(currObjectValue!==null){currObjectValue=(key in currObjectValue)?currObjectValue[key]:null}});if(currObjectValue!==null){$(this).text(currObjectValue)}else{console.log('Could not find',translationAttr,'in language ',currentLanguage);$(this).text(translationAttr)}})})}